  //二分查找方法
  private static int binarySearch(int[] srcArray, long des)
  {
    int low  = 0;
    int high = srcArray.length - 1;
    while (low <= high) {
      int middle = (low + high) / 2;
      if (des == srcArray[middle]) {
        return middle;
      }

      if (des < srcArray[middle]) {
        high = middle - 1;
      } else {
        low = middle + 1;
      }
    }
    return -1;
  }
